body {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 14px;
    line-height: 1.428571429;
    background-color: #ffffff;
    color: #2F3230;
    padding: 0;
    margin: 0;
}
section {
    display: block;
    padding: 0;
    margin: 0;
}
.container {
    margin-left: auto;
    margin-right: auto;
    padding: 0 10px;
}
.additional-info {
    background-repeat: no-repeat;
    background-color: #293A4A;
    color: #FFFFFF;
}
.additional-info-items {
    padding: 20px;
    min-height: 193px;
}
.info-heading {
    font-weight: bold;
    text-align: left;
    word-break: break-all;
    width: 100%;
}
.status-reason {
    font-size: 200%;
    display: block;
    color: #CCCCCC;
}
.reason-text {
    margin: 20px 0;
    font-size: 16px;
}
.info-heading {
    font-size: 190%;
}
.reason-text {
    font-size: 140%;
}

 a#dynamicProviderLink, a#dynamicProviderLink:hover, a#dynamicProviderLink:active, a#dynamicProviderLink:visited {
    color: white;
}

@media (min-width: 768px) {
    .additional-info {
        position: relative;
        overflow: hidden;
        background-image: none;
    }
    .additional-info-items {
        padding: 20px;
    }
    .container {
        width: 90%;
    }
    .status-reason {
        display: inline;
    }
}
@media (min-width: 992px) {
    .additional-info {
        background-image: url(data:image/png;base64,);
    }
    .container {
        width: 70%;
    }
    .status-reason {
        font-size: 450%;
    }
    .info-heading {
        font-size: 200%;
    }
    .reason-text {
        font-size: 160%;
    }
}

